import { useEffect } from "react";
import './pokemon.css';
export default function PokemonBasic({ p }) {


  const imgDir = `${process.env.PUBLIC_URL}/data/`;

  

  useEffect(() => {
    if(p) {
      console.log(p.stages.length)
    }
  }, [p]);

  const UpperCase = (word) => {

    word = word.charAt(0).toUpperCase() + word.slice(1)

    if(word.includes("_")) {
      console.log('yes')
    } return word
  }


  const BasicInfo = ({name, value}) => 
    <p><span>{UpperCase(name)}</span> {UpperCase(value)}</p>;

  const evoLength = p.stages.length;

  
  

  return (
    <div>
      <img 
        class="p-basic-img"
        src={`${imgDir}${p.pokemonId}/${evoLength}.png`} 
        alt={`${p.pokemonId} image` }/>
      <p className="p-name">{UpperCase(p.pokemonId)}</p>
      <BasicInfo name={"Role"} value={p.role}/>
      <BasicInfo name={"Dificulty"} value={p.difficulty}/>
      <BasicInfo name={"Type"} value={p.type} />
      <BasicInfo name={"Style"} value={p.style} />
    </div>
  );
}
