import { useEffect } from "react"
import './css/moves.css';
import CardHeader from "./card/CardHeader";
import Card from "./card/Card";
import CardBody from "./card/CardBody";

export default function Moves({p}) {
    const imgDir = `${process.env.PUBLIC_URL}/data/`;
    
    useEffect(() => {
        console.log(p
            )
    }, [p])


    var moveSlots = [ p.moves.slot1, p.moves.slot2 ];

    return (
        <div>
            <BasicCard 
                title={"Basic Moves"}
                p={p}
                moves={p.moves}/>
                {console.log(moveSlots)}
            <div className="move-grid">
                {moveSlots.map((m, i) => {
                <Card 
                    title={"Slot 1"}
                    p={p}>
                    <CardBody>
                        <div className="move-group">
                        { m.map(m => (
                            <div
                                key={m.moveId}>
                                <MoveRow 
                                    p={p}
                                    title={m.moveId}
                                    url={`${imgDir}${p.pokemonId}/moves/${m.moveId}.png`}/>
                                <p><span>Style: </span>{m.style}</p>
                                <p><span>Category: </span>{m.category}</p>
                                <p><span>Cooldown: </span>{m.cooldown}</p>
                                <p><span>Level: </span>{m.level}</p>
                                {m.upgrade ?
                                    <p><span>Upgrade: </span> {m.upgrade}</p>
                                    : null 
                                }
                            </div>
                        ))}
                        </div>
                    </CardBody>
                </Card>
                })}
                <Card 
                    title={"Slot 1"}
                    p={p}>
                    <CardBody>
                        <div className="move-group">
                        { p.moves.slot1.map(m => (
                            <div
                                key={m.moveId}>
                                <MoveRow 
                                    p={p}
                                    title={m.moveId}
                                    url={`${imgDir}${p.pokemonId}/moves/${m.moveId}.png`}/>
                                <p><span>Style: </span>{m.style}</p>
                                <p><span>Category: </span>{m.category}</p>
                                <p><span>Cooldown: </span>{m.cooldown}</p>
                                <p><span>Level: </span>{m.level}</p>
                                {m.upgrade ?
                                    <p><span>Upgrade: </span> {m.upgrade}</p>
                                    : null 
                                }
                            </div>
                        ))}
                        </div>
                    </CardBody>
                </Card>
                <Card 
                    title={"Slot 2"}
                    p={p}>
                    <CardBody>
                        <div className="move-group">
                        { p.moves.slot2.map(m => (
                            <div
                                key={m.moveId}>
                                <MoveRow 
                                    p={p}
                                    title={m.moveId}
                                    url={`${imgDir}${p.pokemonId}/moves/${m.moveId}.png`}/>
                                <p><span>Style: </span>{m.style}</p>
                                <p><span>Category: </span>{m.category}</p>
                                <p><span>Cooldown: </span>{m.cooldown}</p>
                                <p><span>Level: </span>{m.level}</p>
                                {m.upgrade ?
                                    <p><span>Upgrade: </span> {m.upgrade}</p>
                                    : null 
                                }
                            </div>
                        ))}
                        </div>
                    </CardBody>
                </Card>
            </div>
        </div>
    )
}


function BasicCard({title, moves, p}) {
    const imgDir = `${process.env.PUBLIC_URL}/data/`;
    return (
        <div className="move-card">
            <CardHeader 
                p={p}
                title={title}/>
            <div className="basic-card-body">
                <div className="move-card-body">
                    <div className="move-group">
                        <p className="move-type">Basic</p>
                        <MoveRow 
                            p={p}
                            title={moves.basic.moveId}
                            url={`${imgDir}/standard.png`}/>
                        <p><span>Style: </span>{moves.basic.style}</p>
                        <p><span>Boost Count: </span>{moves.basic.boostCount}</p>
                        <p><span>Boost Style: </span>{moves.basic.boostStyle}</p>
                    </div>
                </div>
                <div className="move-card-body">
                    <div className="move-group">
                        <p className="move-type">Unite</p>
                        <MoveRow 
                            p={p}
                            title={moves.unite.moveId}
                            url={`${imgDir}${p.pokemonId}/moves/unite.png`}/>
                    </div>
                </div>
            </div>
        </div>
    )
}

function MoveRow({ title, url}) {
    const upperCase = (word) => {
        word = word.charAt(0).toUpperCase() + word.slice(1)
        if(word.includes("_")) {
          var splitWord = word.split("_")
          splitWord[1] = splitWord[1].charAt(0).toUpperCase() + splitWord[1].slice(1)
          word = splitWord.join().replaceAll(",", " ")
        } return word
      }
    return (
            <div 
                className="icon-name-row">
                <img
                    className="move-icon" 
                    src={url}
                    alt='hello'/>
                <span>{upperCase(title)}</span>
            </div>
    )
}
