import { useContext, useEffect } from "react";
import './pokemon.css';


export default function PokemonBasic({ p }) {
  const imgDir = `${process.env.PUBLIC_URL}/data/`;


  const UpperCase = (word) => {
    word = word.charAt(0).toUpperCase() + word.slice(1)
    if(word.includes("_")) {
      var splitWord = word.split("_")
      splitWord[1] = splitWord[1].charAt(0).toUpperCase() + splitWord[1].slice(1)
      word = splitWord.join().replace(",", " ")
    } return word
  }


  const BasicInfo = ({value}) => 
    <div className="basic-info"><p>{UpperCase(value)}</p></div>;

  const evoLength = p.stages.length;

  return (
    <div>
      <div
        className="pokemon-card__background">
<div className="halftone">
  <svg class="halftone__svg" aria-hidden="true" viewBox="0 0 651 650"></svg></div>
      </div>
      <img 
        className={`p-basic-img ${p.role} `}
        src={`${imgDir}${p.pokemonId}/${evoLength}.png`} 
        alt={`${p.pokemonId} image` }/>
      <div
          className="p-info">
        <p className="p-name">{UpperCase(p.pokemonId)}</p>
        <div className="flex-row">
          <BasicInfo value={p.role}/>
          <BasicInfo value={p.style} />
        </div>
        <div className="flex-row">
          <BasicInfo value={p.difficulty}/>
          <BasicInfo value={p.type} />
        </div>
      </div>
    </div>
  );
}
