import { useState, useEffect } from "react";
import "./roster.css";
import PokemonBasic from "../pokemon/PokemonBasic.jsx";
import { usePokemonProvider } from "../context/PokemonContext";
import SelectPokemon from "../components/pokemonSort/SelectPokemon";
import { useSelectProvider } from "../context/SelectContext";

export default function Roster() {
  const { roster } = usePokemonProvider();
  const { roles, styles, difficulty, type } = useSelectProvider();

  const filters = data => {
    let dataCopy = data;
    //Role Start
    if (roles !== "all") {
      // (1 Roll) && (2 Style)
      if (styles !== "all") {
        // (1 Role) && (2 style) && (3 Difficulty)
        if (difficulty !== "all") {
          // (1 Role) && (2 style) && (3 Difficulty) && ( 4 type )
          if (type !== "all") {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.style === styles &&
              dataCopy.difficulty === difficulty &&
              dataCopy.type === type;
          } else {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.style === styles &&
              dataCopy.difficulty === difficulty;
          }
        } else if (type !== "all") {
          dataCopy =
            dataCopy.role === roles &&
            dataCopy.style === styles &&
            dataCopy.type === type;
        } else {
          dataCopy = dataCopy.role === roles && dataCopy.style === styles;
        }
      } else if (difficulty !== "all") {
        if (styles !== "all") {
          if (type !== "all") {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.style === styles &&
              dataCopy.difficulty === difficulty &&
              dataCopy.type === type;
          } else {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.style === styles &&
              dataCopy.difficulty === difficulty;
          }
        } else if (type !== "all") {
          if (styles !== "all") {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.style === styles &&
              dataCopy.type === type &&
              dataCopy.difficulty === difficulty;
          } else {
            dataCopy =
              dataCopy.role === roles &&
              dataCopy.difficulty === difficulty &&
              dataCopy.type === type;
          }
        } else {
          dataCopy =
            dataCopy.role === roles && dataCopy.difficulty === difficulty;
        }
      } else if (type !== "all") {
        dataCopy = dataCopy.role === roles && dataCopy.type === type;
      } else {
        dataCopy = dataCopy.role === roles;
      }
    }

    return dataCopy;
  };

  const filterData = data => {
    let dataCopy = data;
    var all = "all";
    //start (1))
    if (roles !== all) {
      // start (2)
    } else if (styles !== all) {
      //start (3)
    } else if (difficulty !== all) {
      if (type !== all) {
        dataCopy = dataCopy.difficulty === difficulty && dataCopy.type === type;
      } else {
        dataCopy = dataCopy.difficulty === difficulty;
      }
      //Start (4)
    } else if (type === all) {
      dataCopy = dataCopy.type === type;
    }
    return dataCopy;
  };

  if (!roster) return <p>Loading</p>;

  return (
    <div className="roster">
      <h1>Roster</h1>
      <p>
        <span>Roles:</span> {roles}
      </p>
      <p>
        <span>Styles:</span> {styles}
      </p>
      <p>
        <span>Difficulty:</span> {difficulty}
      </p>
      <p>
        <span>Type:</span> {type}
      </p>

      <div>
        <SelectPokemon />
      </div>
      <div className="p-basic-grid">
        {roster
          .filter(p => filterData(p))
          .map(p => (
            <PokemonBasic key={p.pokemonId} p={p} />
          ))}
      </div>
    </div>
  );
}
