import { useState, useEffect } from "react";
import "./roster.css";
import PokemonBasic from "../pokemon/PokemonBasic.jsx";
import { usePokemonProvider } from "../context/PokemonContext";
import SelectPokemon from "../components/pokemonSort/SelectPokemon";
import { useSelectProvider } from "../context/SelectContext";

export default function Roster() {
  const [role, setRole] = useState("all");
  const { roster } = usePokemonProvider();

  const { roles, styles, difficulty, type } = useSelectProvider();

  const handleChange = e => {
    setRole(e.target.value);
  };

  var allRoles = [
    { label: "All Pokemon", value: "all" },
    { label: "Attacker", value: "attacker" },
    { label: "All Rounder", value: "all_rounder" },
    { label: "Defender", value: "defender" },
    { label: "Speedster", value: "speedster" },
    { label: "Supporter", value: "supporter" },
  ];

  if (!roster) return <p>Loading</p>;

  return (
    <div className="roster">
      <h1>Roster</h1>
      <p>
        <span>Roles:</span> {roles}
      </p>
      <p>
        <span>Styles:</span> {styles}
      </p>
      <p>
        <span>Difficulty:</span> {difficulty}
      </p>
      <p>
        <span>Type:</span> {type}
      </p>

      <div>
        <p>{role}</p>
        <SelectPokemon
          selections={allRoles}
          value={role}
          handleChange={handleChange}
        />
      </div>
      <div className="p-basic-grid">
        {roster
          .filter(p => (role !== "all" ? p.role === role : p))
          .map(p => (
            <PokemonBasic key={p.pokemonId} p={p} />
          ))}
      </div>
    </div>
  );
}
