import { useEffect } from "react"
import './css/moves.css';
import BattleIcon from "./icon/BattleIcon";
import Halftone from "./Halftone";

export default function Moves({p}) {

    useEffect(() => {
        console.log(p.moves)
    }, [p])

    return (
        <Table 
            title={"Moves"}
            p={p}
            moves={p.moves}/>
    )
}

function Table({title, moves, p}) {
    const imgDir = `${process.env.PUBLIC_URL}/data/`;

    return (
        <table>
            <thead className="table-header">
                <tr>
                    <th>
                        <Halftone 
                            styles="card"
                            color={p.role}/>
                        <div className="table-header">
                            <BattleIcon />
                            <p>{title}</p>
                        </div>
                    </th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <th></th>
                </tr>
                <tr>
                    <td>Basic Move</td>
                    <td>{moves.basic.moveId}</td>
                    <td>{moves.basic.style}</td>
                </tr>
                <tr>
                    <th>Unite Move</th>
                </tr>
                <tr>
                    <MoveIconRow 
                        p={p}
                        title={moves.unite.moveId}
                        url={`${imgDir}${p.pokemonId}/moves/unite.png`}/>
                </tr>
                <tr>
                    <td>Slot 2</td>
                    <td>Francisco Chang</td>
                </tr>
            </tbody>
            <thead>
            <tr>
                <MoveIconRow 
                    p={p}
                    title={moves.unite.moveId}
                    url={`${imgDir}${p.pokemonId}/moves/unite.png`}/>
                </tr>
            </thead>
        </table>
    )
}

function MoveIconRow({p, title, url}) {
    
    const upperCase = (word) => {
        word = word.charAt(0).toUpperCase() + word.slice(1)

        if(word.includes("_")) {
          var splitWord = word.split("_")
          splitWord[1] = splitWord[1].charAt(0).toUpperCase() + splitWord[1].slice(1)
          word = splitWord.join().replaceAll(",", " ")
        } return word
      }
    

    return (
        <div 
            className="icon-name-row">
            <img
                className="move-icon" 
                src={url}
                alt='hello'/>
            <span>{upperCase(title)}</span>
        </div>
    )
}
