import { useState, useEffect } from "react";
import "./roster.css";
import PokemonBasic from "../pokemon/PokemonBasic.jsx";
import { usePokemonProvider } from "../context/PokemonContext";

export default function Roster() {
  const [sort, setSort] = useState(null);

  const { roster } = usePokemonProvider();

  const handleClick = e => {
    setSort(e.target.value);
  };

  function Roles() {
    var allRoles = [
      "attacker",
      "all_rounder",
      "defender",
      "speedster",
      "supporter",
    ];

    return (
      <div>
        {allRoles.map(role => (
          <button onClick={handleClick} value={role}>
            {role}
          </button>
        ))}
      </div>
    );
  }

  if (!roster) return <p>Loading</p>;

  return (
    <div className="roster">
      <h1>Roster</h1>
      <div>
        <p>{sort}</p>
        <Roles />
      </div>
      <div className="p-basic-grid">
        {roster.map(p => (
          <PokemonBasic key={p.pokemonId} p={p} />
        ))}
      </div>
    </div>
  );
}
