import { useEffect } from "react"
import './css/moves.css';
import BattleIcon from "./icon/BattleIcon";
import Halftone from "./Halftone";

export default function Moves({p}) {

    useEffect(() => {
        console.log(p
            )
    }, [p])

    return (
        <div className="move-row">
        <Card 
            title={"Slot 1"}
            p={p}
            moves={p.moves}/>
        <Card 
            title={"Slot 2"}
            p={p}
            moves={p.moves}/>
        </div>
    )
}

function Card({title, moves, p}) {
    const imgDir = `${process.env.PUBLIC_URL}/data/`;

    return (

        <div className="move-card">
            <div className="move-card-header">
                <Halftone 
                    styles="card"
                    color={p.role}/>
                <div className="table-header">
                    <BattleIcon />
                    <p>{title}</p>
                </div>
            </div>
            <div className="move-card-body">
                <div className="move-group">
                    <p className="move-type">Unite Move</p>
                    <MoveRow 
                        p={p}
                        title={moves.unite.moveId}
                        url={`${imgDir}${p.pokemonId}/moves/unite.png`}/>
                </div>
                <div className="move-group">
                    <p className="move-type">Slot 1</p>
                    { moves.slot1.map(m => (
                        <div
                            key={m.moveId}>
                            <MoveRow 
                                p={p}
                                title={m.moveId}
                                url={`${imgDir}${p.pokemonId}/moves/${m.moveId}.png`}/>
                        </div>
                    ))}
                </div>
            </div>
        </div>

    )
}

function MoveRow({p, title, url}) {
    
    const upperCase = (word) => {
        word = word.charAt(0).toUpperCase() + word.slice(1)

        if(word.includes("_")) {
          var splitWord = word.split("_")
          splitWord[1] = splitWord[1].charAt(0).toUpperCase() + splitWord[1].slice(1)
          word = splitWord.join().replaceAll(",", " ")
        } return word
      }
    

    return (
            <div 
                className="icon-name-row">
                <img
                    className="move-icon" 
                    src={url}
                    alt='hello'/>
                <span>{upperCase(title)}</span>
            </div>
    )
}
