import { useEffect, useState } from "react";
import { useSelectProvider } from "../../context/SelectContext";


export default function SelectPokemon({ value, selections }) {

    const { roles,
        setRoles,
        styles,
        setStyles,
        difficulty,
        setDifficulty,
        type,
        setType } = useSelectProvider();

    var allRole = [
        { label: "All Pokemon", value: "all" },
        { label: "Attacker", value: "attacker" },
        { label: "All Rounder", value: "all_rounder" },
        { label: "Defender", value: "defender" },
        { label: "Speedster", value: "speedster" },
        { label: "Supporter", value: "supporter" }
      ];

    var allStyle = [
        { label: "All Pokemon", value: "all" },
        { label: "Special", value: "special" },
        { label: "Physical", value: "physical" }
    ];

    var allDifficulty = [
        { label: "All Pokemon", value: "all" },
        { label: "Novice", value: "novice" },
        { label: "Intermeediate", value: "intermeediate" },
        { label: "Expert", value: "expert" }
    ];

    var allType = [
        { label: "All Pokemon", value: "all" },
        { label: "Melee", value: "melee" },
        { label: "Ranged", value: "ranged" }
    ];

    const handleChange = (e) => {

        console.log(e.target.id, e.target.value)
        switch(e.target.id) {
            case 'roles':
                setRoles(e.target.value)
                break;
            case 'styles':
                setStyles(e.target.value)
                break;
            case 'difficulty':
                setDifficulty(e.target.value)
                break;
            case 'type':
                setType(e.target.value)
                break;
        }
    }
    

    return (
      <div>
        <Select 
            id="roles" 
            value={roles} 
            handleChange={handleChange} 
            selections={allRole}/>
        <Select 
        id="styles"
            value={styles} 
            handleChange={handleChange} 
            selections={allStyle}/>
        <Select 
            id="difficulty"
            value={difficulty} 
            handleChange={handleChange} 
            selections={allDifficulty}/>
        <Select 
            id="type"
            value={type} 
            handleChange={handleChange} 
            selections={allType}/>
      </div>
    );
  }
  

function Select({ id, value, handleChange, selections }) {
    return (
      <select
        value={value}
        name="role slect"
        id={id}
        onChange={handleChange}
      >
        {selections.map(role => (
          <option key={role.value} value={role.value}>
            {role.label}
          </option>
        ))}
      </select>
    );
  }
