import { useState, useEffect } from "react";
import "./roster.css";
import PokemonBasic from "../pokemon/PokemonBasic.jsx";
import { usePokemonProvider } from "../context/PokemonContext";

export default function Roster() {
  const [sort, setSort] = useState("all");

  const { roster } = usePokemonProvider();

  const handleChange = e => {
    setSort(e.target.value);
  };

  if (!roster) return <p>Loading</p>;

  return (
    <div className="roster">
      <h1>Roster</h1>
      <div>
        <p>{sort}</p>
        <Roles value={sort} handleChange={handleChange} />
      </div>
      <div className="p-basic-grid">
        {roster
          .filter(p => (sort !== "all" ? p.role === sort : p))
          .map(p => (
            <PokemonBasic key={p.pokemonId} p={p} />
          ))}
      </div>
    </div>
  );
}

function Roles({ value, handleChange }) {
  var allRoles = [
    { label: "All", value: "all" },
    { label: "Attacker", value: "attacker" },
    { label: "All Rounder", value: "all_rounder" },
    { label: "Defender", value: "defender" },
    { label: "Speedster", value: "speedster" },
    { label: "Supporter", value: "supporter" },
  ];

  return (
    <select
      value={value}
      name="role slect"
      id="role-select"
      onChange={handleChange}
    >
      {allRoles.map(role => (
        <option key={role.value} value={role.value}>
          {role.label}
        </option>
      ))}
    </select>
  );
}
